mapper.select:
SELECT *
FROM AgeGroupProductHits
WHERE age_group = #{ageGroup}
  AND productID = #{productId}

mapper.update:
UPDATE AgeGroupProductHits
SET hit = hit + 1
WHERE age_group = #{ageGroup}
  AND productID = #{productId}

mapper.select:
SELECT productID
FROM AgeGroupProductHits
WHERE age_group = #{ageGroup}
ORDER BY hit DESC
    LIMIT 3;
