mapper.select:
SELECT *
FROM PreferenceProductHits
WHERE preference = #{preference}
  AND productID = #{productId}

mapper.update:
UPDATE PreferenceProductHits
SET hit = hit + 1
WHERE preference = #{preference}
  AND productID = #{productId}

mapper.select:
SELECT productID
FROM PreferenceProductHits
WHERE preference = #{preference}
ORDER BY hit DESC
    LIMIT 3;
